use std::env;
use std::fs::File;
use std::io::prelude::*;

fn main() {

let str_old = String::from("
");



let mut f_rna_codon_table = File::open("poem.txt").expect("file not found");

let mut rna_codon_table = String::new();
f_rna_codon_table.read_to_string(&mut rna_codon_table)
        .expect("something went wrong reading the file");


let dlina = rna_codon_table.len() - 6;

    fn piupiu(str: String, table: String, dl: usize) -> String {
        let mut str_new1 = String::from(""); 
        for i in 0..str.len()/3 {
            if &str[i*3..i*3+3] == "GGG" {
                str_new1.push('G');
            } else { 
                for j in 0..dl {
                    if &str[i*3..i*3+3] == &table[j..j+3] {
                        if &table[j+4..j+8] == "Stop"{
                            str_new1.push_str(" ");
                        } else {
                            str_new1.push_str(&table[j+4..j+5]);
                            }
                    }
                }
                }
        }
    return str_new1;
    }

let b = piupiu(str_old, rna_codon_table, dlina);
    println!("b: {}", b);
}
